public class LearningToWrite2Table{
	public static void main (String[] args)
	{
		int n=2;
		System.out.println("Table value of 2 is" );
		for (int i = 1;i<=20;i++)
			System.out.println(+ i+"*"+n+"="+i*n);
		
	}
	
}